import React, {useState, useEffect} from "react";
import {data} from './data';

const Articolo = () => {
  const {id, title, body} = useState(data);

  return (
    <article key={id}>
      <div style={{position: 'relative', height: 'fit-content'}}>
      <h4>{title}</h4>
      <div className="underline"></div>
      </div>
      <p>{body}</p>
    </article>
  );
};

export default Articolo;
